import React, { Component } from "react";
import MenuItem from "./MenuItem";

class MenuCategory extends Component {
	state = {};

	constructor(props) {
		super(props);
		this.state = {
			items: require("./menu/" + this.props.id + ".json").items
		};
	}

	itemSelected = () => {};

	render() {
		var items = this.state.items.map(item => {
			return (
				<MenuItem
					id={item.id}
					price={item.price}
					updateOrder={this.props.itemSelected}
					key={item.id}
				/>
			);
		});

		return (
			<React.Fragment>
				<li>
					<div className="collapsible-header">
						{this.props.id.charAt(0).toUpperCase() +
							this.props.id.substring(1, this.props.id.length)}
					</div>
					<div className="collapsible-body">
						<span>{items}</span>
					</div>
				</li>
			</React.Fragment>
		);
	}

	componentDidUpdate() {}
}

export default MenuCategory;
