import React, { Component } from "react";
import TopNav from "./TopNav";
import AllCategories from "./AllCategories";
import OrderButton from "./OrderButton";

class App extends Component {
	state = {};
	constructor(props) {
		super(props);

		let order = {
			filling: "none",
			beans: "none",
			options: "none",
			rice: "none",
			side: "none",
			total: "0.00"
		};

		let orderButton = (
			<ul className="collapsible">
				<OrderButton order={order} />
			</ul>
		);

		let finalOrderMap = new Map();
		this.state = { orderButton, finalOrderMap };
	}

	parseFinalOrder = finalOrderMap => {
		const newOrder = JSON.parse(JSON.stringify(this.state.order));

		finalOrderMap.forEach(item => {
			// this.state.orderButton.filling = "sjkljsdf";
		});

		this.state.order = newOrder;
		this.state.orderButton.props.children.props.order.filling.setState({
			order: newOrder
		});
	};

	render() {
		return (
			<div className="App">
				<TopNav />
				<AllCategories
					orderButton={this.state.orderButton}
					parseFinalOrder={this.parseFinalOrder}
				/>
			</div>
		);
	}
}

export default App;
