import React, { Component } from "react";
import TopNav from "./TopNav";
import AllCategories from "./AllCategories";
import OrderButton from "./OrderButton";

class App extends Component {
	state = {};
	constructor(props) {
		super(props);

		let order = {
			filling: "none",
			beans: "none",
			options: "none",
			rice: "none",
			side: "none",
			total: "0.00"
		};

		let orderButton = (
			<OrderButton
				order={order}
				updateState={newState => {
					this.state.setState(newState);
				}}
			/>
		);

		let finalOrderMap = new Map();
		this.state = { orderButton, finalOrderMap };
	}

	parseFinalOrder = finalOrderMap => {
		const newOrder = JSON.parse(JSON.stringify(this.state.orderButton.props.order));

		// checking all categories
		let keysToUpdate = [...finalOrderMap.keys()].filter(item => {
			return finalOrderMap.get(item)[0] !== undefined;
		});

		keysToUpdate.map(item => {
			newOrder[item] = finalOrderMap.get(item);
		});

		this.state.orderButton.props.updateState();
		console.log(this.state.orderButton.props.order);
		// this.state.orderButton.setState({ order: newOrder });
		// console.log(this.state.order);
		// this.state.orderButton.setState({
		// 	order: newOrder
		// });
	};

	render() {
		return (
			<div className="App">
				<TopNav />
				<AllCategories orderButton={this.state.orderButton} parseFinalOrder={this.parseFinalOrder} />
			</div>
		);
	}
}

export default App;
