import React, { Component } from "react";
import MenuItem from "./MenuItem";

class MenuCategory extends Component {
	state = {};

	constructor(props) {
		super(props);
		var items = require("./menu/" + this.props.id + ".json").toArray();
		this.state = { items };
	}

	render() {
		{
			Array.from(this.state.items).reduce((items, item) => {
				return items.push(<MenuItem id={item.id} price={item.price} />);
			});
		}
		return <div className="container"></div>;
	}
}

export default MenuCategory;
