import React, { Component } from "react";

class SelectButton extends Component {
	state = {};
	constructor(props) {
		super(props);

		this.state = this.props.order;
	}

	updateState(newState) {
		this.setState(newState);
	}

	parseList(array) {
		let toReturn = "";
		if (array === "n") return "None Selected";
		toReturn = array.reduce((accumulator, item) => {
			return accumulator + item + ", ";
		});

		return toReturn;
	}

	render() {
		return (
			<React.Fragment>
				<li>
					<div className="collapsible-header">Order</div>
					<div className="collapsible-body">
						<div className="col s12 m6">
							<div className="card white darken-4 ">
								<div className="card-content black-text">
									<p>{"Filling: " + this.parseList(this.state.filling[0])}</p>
									<p>{"Beans:   " + this.parseList(this.state.beans[0])}</p>
									<p>{"Options: " + this.parseList(this.state.options[0])}</p>
									<p>{"Rice:    " + this.parseList(this.state.rice[0])}</p>
									<p>{"Sides:   " + this.parseList(this.state.side[0])}</p>
									<p>
										<br />
									</p>{" "}
									<p>{"Delivery: $" + this.state.total * 0.06}</p>
									<p>{"Taxes:    $" + this.state.total * 0.2}</p>
									<p>{"Total:    $" + this.state.total * (1 + 0.2 + 0.06)}</p>
								</div>
							</div>
						</div>
						<div className="col s12 m6">
							<div className="card waves-effect white darken-4 ">
								<div className="card-content black-text">
									<p>Order</p>
								</div>
							</div>
						</div>
					</div>
				</li>
			</React.Fragment>
		);
	}
}

export default SelectButton;
