import React, { Component } from "react";
import MenuCategory from "./MenuCategory";
import OrderButton from "./OrderButton";

class AllCategories extends Component {
	state = {};

	constructor(props) {
		super(props);
		this.state.finalOrderMap = new Map();
		["filling", "rice", "beans", "topthingsoff", "options", "side"].map(
			itemName => {
				return this.state.finalOrderMap.set(itemName, [undefined, 0]);
			}
		);

		this.state.orderTotal = 0;
	}

	orderUpdated = (key, selectedItemsMap, categoryTotal) => {
		this.state.finalOrderMap.set(key, [selectedItemsMap, categoryTotal]);
		this.props.parseFinalOrder();
	};

	render() {
		return (
			<div className="container">
				<ul className="collapsible">
					<MenuCategory
						id="filling"
						orderUpdated={this.orderUpdated}
					/>
				</ul>
				<ul className="collapsible">
					<MenuCategory id="rice" orderUpdated={this.orderUpdated} />
				</ul>
				<ul className="collapsible">
					<MenuCategory id="beans" orderUpdated={this.orderUpdated} />
				</ul>
				<ul className="collapsible">
					<MenuCategory
						id="topthingsoff"
						orderUpdated={this.orderUpdated}
					/>
				</ul>
				<ul className="collapsible">
					<MenuCategory
						id="options"
						orderUpdated={this.orderUpdated}
					/>
				</ul>
				<ul className="collapsible">
					<MenuCategory id="side" orderUpdated={this.orderUpdated} />
				</ul>
				{this.props.orderButton}
			</div>
		);
	}
}

export default AllCategories;
