import React, { Component } from "react";
import TopNav from "./TopNav";
import AllCategories from "./AllCategories";
import OrderButton from "./OrderButton";

class App extends Component {
	state = {};
	constructor(props) {
		super(props);

		let order = {
			filling: "none",
			beans: "none",
			options: "none",
			rice: "none",
			side: "none",
			total: "0.00"
		};

		let orderButton = <OrderButton order={order} />;

		let finalOrderMap = new Map();
		this.state = { orderButton, finalOrderMap };
	}

	parseFinalOrder = finalOrderMap => {
		const newOrder = JSON.parse(
			JSON.stringify(this.state.orderButton.props.order)
		);

		const keys = [...this.state.finalOrderMap.keys()];
		console.log(keys);
		for (let i = 0; i < keys.length; i++) {
			console.log(this.state.finalOrderMap.get(keys[i]));
		}

		// this.setState({ order: newOrder });
		// console.log(this.state.orderButton.props);
		// this.state.orderButton.setState({
		// 	order: newOrder
		// });
	};

	render() {
		return (
			<div className="App">
				<TopNav />
				<AllCategories
					orderButton={this.state.orderButton}
					parseFinalOrder={this.parseFinalOrder}
				/>
			</div>
		);
	}
}

export default App;
