import React, { Component } from "react";
import MenuItem from "./MenuItem";

class MenuCategory extends Component {
	state = {};

	constructor(props) {
		super(props);
		let items = require("./menu/" + this.props.id + ".json").items;
		const selectedItemsMap = new Map();
		items.map(menuItem => {
			return selectedItemsMap.set(menuItem, false);
		});

		this.state = {
			items,
			selectedItemsMap,
			categoryTotal: 0
		};
	}

	itemSelected = (id, selected, price) => {
		this.state.selectedItemsMap.set(id, selected);
		this.props.orderUpdated(this.props.key, this.state.selectedItemsMap);
		this.setState({ categoryTotal: this.state.categoryTotal + price }); // POSSIBLE ISSUE
	};

	render() {
		var items = this.state.items.map(item => {
			return (
				<MenuItem
					key={item.id}
					price={item.price}
					updateOrder={this.itemSelected}
					key={item.id}
				/>
			);
		});

		return (
			<React.Fragment>
				<li>
					<div className="collapsible-header">
						{this.props.id.charAt(0).toUpperCase() +
							this.props.id.substring(1, this.props.id.length)}
					</div>
					<div className="collapsible-body">
						<span>{items}</span>
					</div>
				</li>
			</React.Fragment>
		);
	}

	componentDidUpdate() {}
}

export default MenuCategory;
