import React, { Component } from "react";
import TopNav from "./TopNav";
import AllCategories from "./AllCategories";
import OrderButton from "./OrderButton";

class App extends Component {
	state = {};
	constructor(props) {
		super(props);

		let order = {
			filling: "none",
			beans: "none",
			options: "none",
			rice: "none",
			side: "none",
			total: "0.00"
		};

		let orderButton = <OrderButton order={order} />;

		let finalOrderMap = new Map();
		this.state = { orderButton, finalOrderMap };
	}

	parseFinalOrder = finalOrderMap => {
		const newOrder = JSON.parse(JSON.stringify(this.state.orderButton.props.order));

		const categoryKeys = [...finalOrderMap.keys()];
		var keysToUpdate = categoryKeys.filter(key => {
			return finalOrderMap.get(key)[0] !== undefined;
		});

		keysToUpdate.map(key => {
			let category = finalOrderMap.get(key);
			let selected = category.keys().filter(key => {
				return category.get(key);
			});

			console.log(selected);

			return;
		});

		console.log(newOrder);

		// this.state.orderButton.setState({ order: newOrder });
		// console.log(this.state.order);
		// this.state.orderButton.setState({
		// 	order: newOrder
		// });
	};

	render() {
		return (
			<div className="App">
				<TopNav />
				<AllCategories orderButton={this.state.orderButton} parseFinalOrder={this.parseFinalOrder} />
			</div>
		);
	}
}

export default App;
